$.fn.editor=function(a){$.fn.editor.defaults={edit:true,height:0,styles:""};var b=$.extend({},$.fn.editor.defaults,a);return this.each(function(){var e=$(this);var d=e.attr("id");if(b.edit){e.next(".fs-editor-span:eq(0)").remove();e.css("display","");if(e.is(".idate")){e.next().css("display","");$("#LK"+e.attr("id")).css("display","")}if(e.is(".ilookup")){$("#LK"+e.attr("id")).css("display","")}}else{var c=e.val();if(e.is("select")){c=$("option:selected",e).text()}e.next(".fs-editor-span:eq(0)").remove();var f=$("<span class='fs-editor-span' id='"+(d+"_span")+"' style='white-space: nowrap; "+b.styles+"'></span>").text(c);f.css("text-align",e.css("text-align"));if(e.is(".idecimal")){f.css("text-align","right")}e.css("display","none");if(e.is(".idate")){e.next().css("display","none");$("#LK"+e.attr("id")).css("display","none")}if(e.is(".ilookup")){$("#LK"+e.attr("id")).css("display","none")}f.insertAfter(e)}})};